#include <iostream>
#include <string>
#include <string.h>
#include <algorithm>
using namespace std;
extern "C" {
extern char* shortestPalindrome(char* s);
}

class Solution {
public:
    string shortestPalindrome(string s) {
        int n = s.length();
		if(n<=1)
			return s;
        char *input = (char *)s.c_str();
		bool bIsPalindrome = false;
		int k = 0;
		for(k = n - 1; k>=0 && !bIsPalindrome; k--) {
			int i=0;
			int j=k;
			for(; i<j && input[i] == input[j]; i++,j--);
			if(i>=j)
				bIsPalindrome = true;
		}
		if(k==n-2)
			return s;
		k += 2;
#if 1
        char* output = new char[n+n-k+1];
        int id=0;
        for(id=k;id<n;id++){
            output[n-id-1] = input[id];
        }
        output[n-k] = '\0';
       // s = output + s;
        strcat(output,s.c_str());
        s = output;
        delete output;
#endif

		return s;
	}
};
int main(int argc, char* argv[])
{
#if 1
	string out;
	Solution sol;
	if(argc > 1) {
		string input = argv[1];
		out = sol.shortestPalindrome(input);
	} else {
		out = sol.shortestPalindrome("");
	}
#else
	string out;
	if(argc > 1) {
		string input = argv[1];
		out = shortestPalindrome((char *)input.c_str());
	} else {
		out = shortestPalindrome("");
	}
#endif

	cout << out << endl;
	cout << out.length() << endl;
    return 0;
}
